function UI.info_setup()
GUI = GUI or {}
yCoordHeightTable = yCoordHeightTable or {}
dprTable = dprTable or {}
yParryTable = yParryTable or {}
yDodgeTable = yDodgeTable or {}
yBlockTable = yBlockTable or {}
yShieldBlockTable = yShieldBlockTable or {}
yPhaseTable = yPhaseTable or {}
yKineticTable = yKineticTable or {}
yAbsorbTable = yAbsorbTable or {}
yHitTable = yHitTable or {}
yOrbTable = yOrbTable or {}


GUI.master=Adjustable.Container:new({name = "master", y = "0", x = "0", height = "100%", width = "100%", color = "transparent",
  autoload = true})

GUI.bottom = Adjustable.Container:new({name = "bottom", title = "Gauges",
  height = "6%", y = "-6%", autoLoad = true}, GUI.master)

GUI.right = Adjustable.Container:new({name = "right", y = "0%", title = "Comms",
  height = "100%", x = "-25%", width = "25%", autoLoad = true}, GUI.master)

GUI.left = Adjustable.Container:new({name = "left", x = "0%", y = "0%", title = "Trackers",
  height = "100%", width = "15%", autoLoad = true}, GUI.master)

GUI.middle = Adjustable.Container:new({name = "middle", x = "15%", y = "0%", width = "60%", height = "94%",
  autoWrap = true, title = "Console", autoLoad = true}, GUI.master)

GUI.middle_term = GUI.middle_term or Geyser.MiniConsole:new(
  {name = "middle_term", autoWrap = true,
   x = "1%", y = "1%",
   width = "99%", height = "99%",
   color = "transparent",
   scrollBar = true,
   autoWrap = true,
   fontSize= calcFontSize("main")
  },GUI.middle)

--GUI.top = Adjustable.Container:new({name = "top", x = "0%", y = "0%", height = "10%", width = "100%", autoLoad = true}, GUI.middle)

--GUI.room_term = GUI.room_term or Geyser.MiniConsole:new(
  --{name = "room_term", autoWrap = true,
   --x = "1%", y = "1%",
   --width = "99%", height = "99%",
   --color = "transparent",
   --scrollBar = true,
   --autoWrap = true,
   --fontSize= calcFontSize("main")
  --},GUI.top)

--GUI.damage = Geyser.UserWindow:new({name = "damage", x = "0%", y = "0%", height = "30%", width = "100%", autoLoad = false}, GUI.left)

GUI.bottom:attachToBorder("bottom")
GUI.left:attachToBorder("left")
GUI.right:attachToBorder("right")



GUI.bottom:connectToBorder("left")
GUI.bottom:connectToBorder("right")
GUI.middle:connectToBorder("right")
GUI.middle:connectToBorder("left")
GUI.middle:connectToBorder("bottom")


UI.right_back = UI.right_back or Geyser.Label:new(
  {name = "right_back",
  x = 0, y = 0,
  width = "100%", height = "100%",
  color = "transparent",
  },GUI.right)



UI.right_term = UI.right_term or Geyser.MiniConsole:new(
  {name = "right_term",
   x = "1%", y = "1.5%",
   width = "98%", height = "98%",
   color = "transparent",
   scrollBar = false,
   autoWrap = true,
  },GUI.right)

GUI.dprDisplay = GUI.dprDisplay or
  Geyser.Label:new(
    {
      name = "dprDisplay",
      y = "50%",
      width = "99%",
      height = "25%",
      message = [[<center></center>]],
      },
      GUI.left
   )
GUI.dprDisplay:setFontSize(10)
GUI.dprDisplay:setStyleSheet(
  [[
  font: large center "Garamond";
  border: 1px solid black;
  background-color: "transparent";
  color: #AACCFF;
  border-width: 1px;
  border-style: solid;
  border-radius: 7;
  ]]
)

GUI.dprDisplayTopLabel = GUI.dprDisplayTopLabel or
  Geyser.Label:new(
  {
  fgColor = "white",
  color = "transparent",
  message = [[<center></center>]],
  name = "dprDisplayTopLabel",
  width = "99%",
  height = "10%",
  y = "0%",
  },
  GUI.dprDisplay
  )

 GUI.dprDisplayBotLabel = GUI.dprDisplayBotLabel or
  Geyser.Label:new(
  {
  fgColor = "white",
  color = "transparent",
  message = [[<center></center>]],
  name = "dprDisplayBotLabel",
  width = "99%",
  height = "10%",
  y = "90%",
  },
  GUI.dprDisplay
  )

GUI.dprDisplayBox = GUI.dprDisplayBox or
  Geyser.HBox:new(
    {
    name = "dprDisplayBox",
    width = "98%",
    height = "80%",
    y= "10%",
    color = "transparent",
    },
    GUI.dprDisplay
    )

for i=1,50 do
  GUI[i.."dprRateBar"] = Geyser.Gauge:new(
      {
      name = "GUI"..i.."dprRateBar",
      height = "100%", width = "5px", orientation = "vertical",
      },
      GUI.dprDisplayBox
      )

    GUI[i.."dprRateBar"].front:setStyleSheet([[background-color: rgba(255, 0, 0, 100%);
      border-width: 1px;
      border-height: 1px;
      border-color: black;
      border-style: solid;
      border-radius: 7;
      padding: 1px;
      ]])

    GUI[i.."dprRateBar"].back:setStyleSheet([[background-color: rgba(255, 0, 0, 20%);
      border-width: 1px;
      border-height: 1px;
      border-color: black;
      border-style: solid;
      border-radius: 7;
      padding: 1px;
      ]])


    GUI[i.."dprRateBar"]:hide()
end


end

function UI.setup() cecho("<blue>[ INFO ] -<grey> Activating user interface.\n") UI.info_setup() UI.gauge_setup() defenseBar_setup()
end
function UI.dismantle() --do something in the future end

function defenseBar_setup()

GUI.defDisplay = GUI.defDisplay or
  Geyser.Label:new(
    {
      name = "defDisplay",
      y = "75%",
      width = "99%",
      height = "25%",
      message = [[<center>"I'm real"</center>]],
      },
      GUI.left
   )
GUI.defDisplay:setFontSize(10)

GUI.defDisplay:setStyleSheet(
  [[
  font: large center "Garamond";
  border: 1px solid black;
  background-color: "transparent";
  color: #AACCFF;
  border-width: 1px;
  border-style: solid;
  border-radius: 7;
  ]]
)

GUI.defDisplayBox = GUI.defDisplayBox or
  Geyser.HBox:new(
    {
    name = "defDisplayBox",
    width = "80%",
    height = "80%",
    y= "10%",
    color = "transparent",
    },
    GUI.defDisplay
    )
for i=1,50 do
  GUI[i.. "defRateBar"] = Geyser.VBox:new(
      {
      name = "GUI" ..i.. "defRateBar",
      height = "100%", width = "2%",
      color = "transparent",
      },
      GUI.defDisplayBox
      )
  end
end
